<template>
  <navbar :isHome="false" />
  <view class="img-full-screen">
    <image style="position: absolute; width: 110vw; height: 110vh; left: -10vw; top: -10vh;"
      src="data:image/svg+xml;base64,">
    </image>

    <text
      style="position: absolute; width: 210vw; height: 5vh; left: 35vw; top: 4.5vh; font-size: 4.5vw; font-weight: 800;">
      修改昵称
    </text>

    <view
      style="position: absolute; width: 90vw; height: 5vh; left: 5vw; top: 13vh; background-color: #fff; border-radius: 30rpx;">
    </view>
    <input v-model="inputValue"
      style="position: absolute; width: 65vw; height: 5vh; left: 10vw; top: 13vh; border-radius: 30rpx; font-size: 4vw;">
    <!-- 根据变化 -->
    <text
      style="position: absolute; width: 65vw; height: 5vh; left: 80vw; top: 14vh; border-radius: 30rpx; font-size: 4vw; color: #d1c6b1;">{{inputValue.length}}/24</text>

    <image style="position: absolute; width: 80vw; height: 5vh; left: 8vw; top: 18vh;"
      src="data:image/svg+xml;base64,">
    </image>

    <button
      style="position: absolute; width: 90vw; height: 5vh; left: 5vw; top: 27vh; background-color: #feba0f; border-radius: 80rpx;">
    </button>
    <text @click="modify_username"
      style="position: absolute; width: 90vw; height: 5vh; left: 40vw; top: 28vh; color: white; font-size: 4.2vw; font-weight: 600;">
      确认修改
    </text>
  </view>
</template>

<script setup>
  import {
    ref
  } from 'vue';
  const app = getApp()

  let inputValue = ref('');

  const modify_username = () => {
    uni.setStorageSync('username', inputValue.value)
    app.globalData.utils.request({
      url: '/user/modify/username/',
      method: 'POST',
      data: {
        openid: uni.getStorageSync('openid'),
        username: inputValue.value
      },
      success: res => {
        console.log(res)
      },
      fail: res => {
        console.log(res)
      }
    })
  }
</script>

<style>
  page {
    height: 100vh;
  }

  .img-full-screen {
    height: 100vh;
    width: 100vw;
    background-color: #fff9ed;
  }
</style>