<template>
  <navbar :isHome="false" />
  <view class="img-full-screen">
    <image style="position: absolute; width: 110vw; height: 110vh; left: -10vw; top: -10vh;"
      src="data:image/svg+xml;base64,">
    </image>

    <text
      style="position: absolute; width: 10vw; height: 5vh; left: 40vw; top: 4.5vh; font-size: 4.5vw; font-weight: 800;">
      设置
    </text>

    <!-- 头像 -->
    <image v-if="photo === ''" style="position: absolute; width: 25vw; height: 11.5vh; left: 8vw; top: 10vh;"
      src="https://dida.012331.com/media/uploaded_photo_oPhKb6w1zt7PGXFasszebMyvO9R4_5440.png">
    </image>
    <image v-else style="position: absolute; width: 25vw; height: 11.5vh; left: 8vw; top: 10vh; border-radius: 100rpx;"
      :src="photo">
    </image>
    <image @click="chooseImage" style="position: absolute; width: 7vw; height: 4vh; left: 26vw; top: 18vh;"
      src="data:image/svg+xml;base64,">
    </image>

    <!-- 用户名 -->
    <view style="position: absolute; width: 30vw; height: 5vh; left: 38vw; top: 13vh;">
      <text style="font-weight: 600; font-size: 5vw;"> {{username}} </text>
    </view>

    <!-- 简介 -->
    <view style="position: absolute; width: 50vw; height: 5vh; left: 38vw; top: 17vh;">
      <text style="font-weight: 600; font-size: 4vw;"> {{description}} </text>
    </view>

    <!-- 功能栏 -->
    <image style="position: absolute; width: 90vw; height: 10vh; left: 5vw; top: 27vh;"
      src="data:image/svg+xml;base64,">
    </image>

    <text @click="onChangeUsernameTap"
      style="position: absolute; width: 90vw; height: 5vh; left: 10vw; top: 28vh; font-size: 4.2vw; font-weight: 900;">
      修改昵称
    </text>
    <text @click="onChangeIdentityTap"
      style="position: absolute; width: 90vw; height: 5vh; left: 10vw; top: 33vh; font-size: 4.2vw; font-weight: 900;">
      更改身份
    </text>

    <image style="position: absolute; width: 90vw; height: 10vh; left: 5vw; top: 40vh;"
      src="data:image/svg+xml;base64,">
    </image>

    <text
      style="position: absolute; width: 90vw; height: 5vh; left: 10vw; top: 41vh; font-size: 4.2vw; font-weight: 900;">
      服务条款
    </text>
    <text
      style="position: absolute; width: 90vw; height: 5vh; left: 10vw; top: 46vh; font-size: 4.2vw; font-weight: 900;">
      隐私政策
    </text>

    <button
      style="position: absolute; width: 90vw; height: 5vh; left: 5vw; top: 53vh; background-color: #feba0f; border-radius: 80rpx;">
    </button>
    <text @click="onQuitTap"
      style="position: absolute; width: 90vw; height: 5vh; left: 40vw; top: 54vh; color: white; font-size: 4.2vw; font-weight: 600;">
      退出登陆
    </text>

  </view>
</template>

<script setup>
  import {
    ref
  } from 'vue';

  import {
    onLoad
  } from '@dcloudio/uni-app';

  let openid = ref('')
  let photo = ref('')
  let username = ref('')
  let phone = ref('')
  let description = ref('')

  let photo_copy = ref('')

  onLoad(() => {
    openid.value = uni.getStorageSync('openid')
    photo.value = uni.getStorageSync('photo')
    phone.value = uni.getStorageSync('phone')
    username.value = uni.getStorageSync('username')
    description.value = uni.getStorageSync('description')
  });

  const onChangeUsernameTap = () => {
    uni.navigateTo({
      url: '/pagesC/user/changeUsername'
    })
  }

  const onChangeIdentityTap = () => {
    uni.navigateTo({
      url: '/pagesC/user/changeIdentity'
    })
  }

  const onQuitTap = () => {
    uni.navigateTo({
      url: '/pagesC/index/index'
    })
  }

  const chooseImage = () => {
    uni.chooseImage({
      count: 1,
      sizeType: ['compressed'],
      sourceType: ['album', 'camera'],
      success: (res) => {
        // photo_copy.value = res.tempFilePaths[0];
        uni.uploadFile({
          url: 'https://dida.012331.com/user/modify/photo/',
          filePath: res.tempFilePaths[0],
          name: 'file',
          formData: {
            'openid': uni.getStorageSync('openid'),
          },
          success: (res) => {
            // 注意解析Json
            const data = JSON.parse(res.data);
            if (data.result === 'success') {
              uni.setStorageSync('photo', data.photo_path)
              photo.value = uni.getStorageSync('photo')
              uni.showToast({
                title: '更改成功',
                icon: 'success'
              });
            }
          },
          fail: (err) => {
            console.error('请求失败', err);
            uni.showToast({
              title: '更改失败，请重试',
              icon: 'none'
            });
          }
        });
      }
    })
  }
</script>

<style>
  page {
    height: 100vh;
  }

  .img-full-screen {
    height: 100vh;
    width: 100vw;
    background-color: #fff9ed;
  }
</style>